import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { pdfjs } from 'react-pdf';

pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();

const Cards = ({ item }) => {
  const [pdfName, setPdfName] = useState(null);
  const [allImage, setAllImage] = useState([]);
  const [purchased, setPurchased] = useState(false); // Track if the book is purchased
  const navigate = useNavigate();

  useEffect(() => {
    const getPdf = async () => {
      try {
        const result = await axios.get("http://localhost:4001/get-files");
        setAllImage(result.data.data);
      } catch (error) {
        console.error("Error fetching PDFs:", error.response ? error.response.data : error.message);
      }
    };
    getPdf();
  }, []);

  useEffect(() => {
    const associatedPdf = allImage.find((pdf) => pdf.id === item.id);
    if (associatedPdf) {
      setPdfName(associatedPdf.pdf);
    }
  }, [allImage, item.id]);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://checkout.razorpay.com/v1/checkout.js';
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const createOrder = async () => {
    try {
      const response = await axios.post('http://localhost:4001/create-order', {
        amount: item.price * 100, // Razorpay expects amount in paise
        currency: 'INR',
        receipt: `receipt_${item.id}`,
      });

      return response.data.order;
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Failed to create an order. Please try again.');
    }
  };

  const handlePayment = async () => {
    try {
      // Step 1: Create the Razorpay Order
      const order = await createOrder();
      if (!order) return; // Exit if order creation fails
  
      const options = {
        key: 'rzp_test_dTDDElTlJsbQLC', // Replace with your Razorpay Key ID
        amount: order.amount, 
        currency: order.currency,
        name: 'Acme Corp',
        description: 'Test Transaction',
        image: 'https://example.com/your_logo',
        order_id: order.id, // Razorpay order ID
        handler: async (response) => {
          alert(`Payment Successful! Payment ID: ${response.razorpay_payment_id}`);
  
          // Step 2: Send the purchase request with authentication
          try {
            const token = localStorage.getItem('token'); // Retrieve token from storage
            const purchaseResponse = await axios.post(
              'http://localhost:4001/purchase/buy',
              { bookId: item._id }, // Send book ID
              {
                headers: { Authorization: `Bearer ${token}` }, // Send token in headers
                withCredentials: true, // If your backend uses session cookies
              }
            );
  
            if (purchaseResponse.data.success) {
              alert('Book added to your purchases!');
            } else {
              alert('Failed to add book.');
            }
          } catch (purchaseError) {
            console.error('Error purchasing book:', purchaseError.response?.data || purchaseError.message);
            alert('Purchase failed. Please try again.');
          }
        },
        prefill: {
          name: 'Srijan Kumar',
          email: 'srijan.mishra@example.com',
          contact: '9000090000',
        },
        notes: { address: 'Razorpay Corporate Office' },
        theme: { color: '#3399cc' },
      };
  
      const rzp1 = new window.Razorpay(options);
      rzp1.on('payment.failed', (response) => {
        alert(`Payment Failed: ${response.error.reason}`);
      });
  
      rzp1.open();
    } catch (error) {
      console.error('Error handling payment:', error);
      alert('Payment process failed. Please try again.');
    }
  };

  const showPdf = () => {
    if (pdfName) {
      navigate(`/pdf/${encodeURIComponent(pdfName)}`);
    } else {
      console.error("No PDF available for this item.");
    }
  };

  return (
    <div className='mt-4'>
      <div className="card bg-base-100 w-90 m-2 shadow-xl hover:scale-105 duration-200 bg-white text-black dark:bg-slate-900 dark:text-white dark:border">
        <figure>
          <img src={item.image} alt="Book Cover" />
        </figure>
        <div className="card-body">
          <h2 className="card-title">
            {item.name}
            <div className="badge badge-secondary">{item.category}</div>
          </h2>
          <p>Genre: {item.title}</p>
          <p>Author: <span className="italic">{item.author}</span></p>
          <div className="card-actions justify-between">
            {item.price === 0 ? null : (
              <div
                className="cursor-pointer px-7 py-1 rounded-full border-[2px] hover:bg-pink-500 hover:text-white duration-300 badge-outline"
                onClick={handlePayment}
              >
                ${item.price}
              </div>
            )}

            <div>
              {purchased || item.price === 0 ? (
                pdfName ? (
                  <div className="flex justify-center w-full">
                    <button className="cursor-pointer px-4 py-1 rounded-full border-[2px] hover:bg-pink-500 
                    hover:text-white duration-300 badge-outline" onClick={showPdf}>Read</button>
                  </div>
                ) : (
                  <p>No PDF available</p>
                )
              ) : (
                <p>Purchase to Read</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cards;
