import React, { useEffect, useState } from 'react'
import Cards from './Cards'
import { Link } from 'react-router-dom'
import axios from 'axios';


const Course = () => {
  const[book,setbook] = useState([]);
  useEffect(() =>{
    const getBook = async () => {
    try{
      const res = await axios.get('http://localhost:4001/book');
      console.log(res)
      setbook(res.data);

    }catch(error){
       console.log(error);
    }
  };
  getBook();
  },[])
  return (
    <div className='max-w-screen-2xl container mx-auto md:px-20 px-4 '>
      <div className="mt-14  item-center justify-center text-center pt-8 dark:bg-slate-800 dark:text-white">
        <h1 className=' text-2xl  md:text-4xl dark:text-white text-black'>We are delight to have you{" "} <span className='text-pink-500'>Here! :)</span></h1>
        <p className='mt-10 dark:text-white text-black'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Et, praesentium dicta? Eaque vero praesentium eveniet. Tenetur pariatur impedit quis excepturi quia et, debitis explicabo laudantium ipsa eum voluptate error molestias.</p>
        <Link to="/">
        <button className='bg-pink-500 mt-4 px-4 py-2 rounded-md text-white hover:bg-pink-700 duration-300'>Back</button>

        </Link>
      </div>
      <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-10'>
        {book.map((item) => (
          <Cards item={item} key={item.id} />
        ))}
      </div>
    </div>
  )
}

export default Course
