const Navbar = () => {
  const [auth, setAuth] = useAuth();
  console.log(auth);

  const [theme, setTheme] = useState(localStorage.getItem("theme") ? localStorage.getItem('theme') : "light");
  const element = document.documentElement;

  useEffect(() => {
    if (theme === "dark") {
      element.classList.add("dark");
      localStorage.setItem("theme", "dark");
      document.body.classList.add("dark");
    } else {
      element.classList.remove("dark");
      localStorage.setItem("theme", "light");
      document.body.classList.remove("dark");
    }
  }, [theme]);

  const [sticky, setSticky] = useState(false);
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 0) {
        setSticky(true);
      } else {
        setSticky(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = (
    <>
      <li><Link to="/">Home</Link></li>
      <li><Link to="/course">Course</Link></li>
      <li><Link to="/contact">Contact</Link></li>
      {auth?.user?.role === "admin" && ( // Conditionally render for admin role
        <li><Link to="/srijan">Admin<ProtectedRoute><Srijan /></ProtectedRoute></Link></li>
      )}
      <li><a>About</a></li>
    </>
  );

  return (
    <div className={`max-w-screen-2xl text-black container mx-auto md:px-20 px-1 fixed z-50 dark:bg-slate-900 bg-white shadow-lg dark:text-white bg-base-200 ${sticky ? "sticky-navbar shadow-md bg-base-200 dark:bg-slate-900 dark:text-white transition-all duration-100 ease-in-out" : ""}`}>
      <div className="navbar">
        <div className="navbar-start">
          <div className="dropdown p-1">
            <div tabIndex={0} role="button" className="btn btn-ghost lg:hidden">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h8m-8 6h16" />
              </svg>
            </div>
            <ul tabIndex={0} className="menu space-y-2 menu-sm dark:bg-slate-900 dark:text-white text-black bg-white dropdown-content bg-base-100 rounded-box z-[1] mt-3 w-52 p-2 shadow">
              {navItems}
            </ul>
          </div>
          <Link to="/" className="text-2xl font-bold px-3 py-2 border-2 dark:text-white dark:bg-slate-900 dark:border-white border-black text-black rounded-lg cursor-pointer">
            Book<span className="text-pink-500">Store</span>
          </Link>
        </div>
        <div className="navbar-end space-x-3">
          <div className="navbar-center hidden lg:flex">
            <ul className="menu menu-horizontal px-1">
              {navItems}
            </ul>
          </div>
          <div className="hidden md:block">
            <label className="px-3 py-2 border rounded-md flex items-center gap-2 dark:bg-slate-900 bg-white">
              <input type="text" className="grow outline-none dark:bg-slate-900 dark:text-white bg-white" placeholder="Search" />
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16" fill="currentColor" className="h-4 w-4 opacity-70">
                <path fillRule="evenodd" d="M9.965 11.026a5 5 0 1 1 1.06-1.06l2.755 2.754a.75.75 0 1 1-1.06 1.06l-2.755-2.754ZM10.5 7a3.5 3.5 0 1 1-7 0 3.5 3.5 0 0 1 7 0Z" clipRule="evenodd" />
              </svg>
            </label>
          </div>
          <label className="swap swap-rotate">
            <input type="checkbox" className="theme-controller" value="synthwave" />
            <svg
              onClick={() => setTheme(theme === "light" ? "dark" : "light")}
              className="swap-off h-7 w-7 fill-current"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24">
              <path d="..." />
            </svg>
            <svg
              onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
              className="swap-on h-7 w-7 fill-current"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24">
              <path d="..." />
            </svg>
          </label>
          {auth ? <Logout /> : (
            <div>
              <button className="btn btn-secondary cursor-pointer text-white" onClick={() => document.getElementById("my_modal_3").showModal()}>Login</button>
              <Login />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
