import { useState } from 'react';
import { Document, Page } from 'react-pdf';

function Pdfcomp({ pdfFile }) {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div className='flex flex-col justify-center items-center h-screen bg-gray-900 dark:bg-gray-900 border-none'> {/* Full screen and center alignment */}
      <p className='text-xl p-4 font-bold text-center dark:text-white text-black'>
        Page {pageNumber} of {numPages || 'Loading...'}
      </p>

      {pdfFile ? (
        <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
          {numPages && Array.from({ length: numPages }, (x, i) => (
            <div key={i} className='mb-5 border border-gray-300 p-2 bg-blue-50 w-full overflow-hidden dark:bg-gray-800 dark:border-gray-700'>
              <Page
                pageNumber={i + 1}
                renderAnnotationLayer={false}
                renderTextLayer={false}
                width={600}
                className='mx-auto w-full'
              />
            </div>
          ))}
        </Document>
      ) : (
        <p className='dark:text-white text-black'>No PDF file selected</p>
      )}
    </div>
  );
}

export default Pdfcomp;
