import { useLocation } from 'react-router-dom';
import { useState } from 'react';
import { Document, Page } from 'react-pdf';

function Pdfcomp() {
  const location = useLocation(); // Access the state passed via navigate
  const pdfFile = location.state?.pdfFile; // Retrieve the PDF file from state

  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  if (!pdfFile) {
    return <p>No PDF file selected.</p>;
  }

  return (
    <div className='m-5 p-5 mx-auto border border-rounded border-black max-w-screen-md overflow-hidden'>
      <p className='text-xl font-bold text-center'>
        Page {pageNumber} of {numPages}
      </p>

      <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
        {Array.from({ length: numPages }, (x, i) => (
          <div key={i} className='mb-5 border border-gray-300 p-2 bg-blue-50 w-full overflow-hidden'>
            <Page
              pageNumber={i + 1}
              renderAnnotationLayer={false}
              renderTextLayer={false}
              width={600}
              className='mx-auto w-full'
            />
          </div>
        ))}
      </Document>
    </div>
  );
}

export default Pdfcomp;
