import { useState } from 'react';
import { Document, Page } from 'react-pdf';
import { useLocation } from 'react-router-dom';

function Pdfcomp() {
  const [numPages, setNumPages] = useState();
  const [pageNumber, setPageNumber] = useState(1);

  const location = useLocation();
  const { pdfFile } = location.state || {}; // Retrieve pdfFile from location state

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div className='m-5 p-5 mx-auto border border-rounded border-black max-w-screen-md overflow-hidden'> {/* Add max width and overflow hidden */}
      {pdfFile ? (
        <>
          <p className='text-xl font-bold text-center'>Page {pageNumber} of {numPages}</p>

          <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
            {Array.apply(null, Array(numPages)).map(function (x, i) {
              return (
                <div key={i} className='mb-5 border border-gray-300 p-2 bg-blue-50 w-full overflow-hidden'> {/* Ensure full width and hide overflow */}
                  <Page
                    pageNumber={i + 1}
                    renderAnnotationLayer={false}
                    renderTextLayer={false}
                    width={600} // Set the width to control the size of the PDF
                    className='mx-auto w-full' // Ensure the PDF page fits inside the container
                  />
                </div>
              );
            })}
          </Document>
        </>
      ) : (
        <p>No PDF specified</p>
      )}
    </div>
  );
}

export default Pdfcomp;
