import React from 'react';
import { Link } from 'react-router-dom';

const About = () => {
  return (
    <div className="max-w-3xl mx-auto p-6 text-center">
      <h1 className="text-3xl font-bold mb-4">About Our Bookstore</h1>
      <p className="text-gray-700 text-lg mb-6">
        Welcome to our online bookstore! We offer a wide range of free and paid books across various genres. 
        Whether you love fiction, non-fiction, or educational books, we have something for you. 
        Start exploring today and find your next great read!
      </p>
      
      <Link to="/">
        <button className="bg-pink-500 px-6 py-2 rounded-md text-white hover:bg-pink-700 duration-300">
          Back to Home
        </button>
      </Link>
    </div>
  );
}

export default About;
