import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { pdfjs } from 'react-pdf';


pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();

const Cards = ({ item }) => {
  const [allImage, setAllImage] = useState([]);
  const navigate = useNavigate();
  // Fetch PDFs when the component mounts
  useEffect(() => {
    const getPdf = async () => {
      try {
        const result = await axios.get("http://localhost:4001/get-files");
        setAllImage(result.data.data);
      } catch (error) {
        console.error("Error fetching PDFs:", error.response ? error.response.data : error.message);
      }
    };

    getPdf();
  }, []); // Empty dependency array to run once on mount

  // Define showPdf function before using it
  const showPdf = (pdfName) => {
    // Define what should happen when the user clicks "Buy Now"
    navigate(`/pdf/${encodeURIComponent(pdfName)}`); 
   // window.open(`http://localhost:4001/files/${pdfName}`, '_blank'); // Open the PDF in a new tab
  };

  return (
    <div className='mt-4'>
      <div className="card bg-base-100 w-90 m-2 shadow-xl hover:scale-105 duration-200 bg-white text-black dark:bg-slate-900 dark:text-white dark:border">
        <figure>
          <img src={item.image} alt="Book Cover" />
        </figure>
        <div className="card-body">
          <h2 className="card-title">
            {item.name}
            <div className="badge badge-secondary">{item.category}</div>
          </h2>
          <p>{item.title}</p>
          <div className="card-actions justify-between">
            <div className="cursor-pointer px-7 py-1 rounded-full border-[2px] hover:bg-pink-500 hover:text-white duration-300 badge-outline">
              ${item.price}
            </div>
            <div className='x'>
              {allImage.length === 0 ? (
                <p>No PDFs available</p>
              ) : (
                allImage.map((data) => (
                  <div className="inner" key={data.id}>
                    <h6>Id: {data.id}</h6>
                     <button type="button" className='btn btn-primary' onClick={() => showPdf(data.pdf)}>
                    View PDF
                  </button>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cards;
