import { useState } from 'react';
import { Document, Page } from 'react-pdf';

function Pdfcomp(props) {
  const [numPages, setNumPages] = useState();
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div className='m-5  mx-auto border border-black max-w-screen-md '> {/* Prevent overflow */}
      <p className='text-xl font-bold text-center'>Page {pageNumber} of {numPages}</p>

      <Document file={props.pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
        {Array.from(new Array(numPages), (el, i) => (
          <div key={i} className='mb-5 mx-auto border border-gray-300 p-2 bg-blue-50'>
            <Page
              pageNumber={i + 1}
              renderAnnotationLayer={false}
              renderTextLayer={false}
              width={600} // Control the width of each PDF page
              className='mx-auto' // Center the PDF within the container
            />
          </div>
        ))}
      </Document>
    </div>
  );
}

export default Pdfcomp;
