import React, { useEffect, useState } from 'react';
import axios from 'axios';
// import Cards from "./Cards"; // Import the existing Cards component
import PurchasedBooks from "./PurchasedBooks"; // Import the existing Cards component

const Mybooks = () => {
  const [purchasedBooks, setPurchasedBooks] = useState([]);

  useEffect(() => {
    const fetchPurchasedBooks = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get('http://localhost:4001/purchase/my-purchases', {
          headers: { Authorization: `Bearer ${token}` },

        });

        setPurchasedBooks(response.data.books);

      } catch (error) {
        console.error('Error fetching purchased books:', error.response ? error.response.data : error.message);
      }
    };

    fetchPurchasedBooks();
  }, []);

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4">
      {
        purchasedBooks.length > 0 ? (
          purchasedBooks.map((book) => <PurchasedBooks key={book.id} item={book} />)
        ) : (
          <p className="text-center text-gray-500">No purchased books available.</p>
        )}
    </div>
  );
};

export default Mybooks;
