import { useState } from 'react';
import { Document, Page } from 'react-pdf';

function Pdfcomp(props) {
  const [numPages, setNumPages] = useState();
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div className='m-5 p-5 justify-center items-center mx-auto border  border-black'>
   <p className='text-xl font-bold'>Page {pageNumber} of {numPages}</p>

  <Document file={props.pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
    {Array.apply(null, Array(numPages)).map(function (x, i) {
      return (
        <div key={i} className='m-5 justify-center items-center border border-gray-300 p-2 bg-blue-50'> {/* Add margin bottom for separation */}
          <Page
            pageNumber={i + 1}
            renderAnnotationLayer={false}
            renderTextLayer={false}
          />
        </div>
      );
    })}
  </Document>
</div>
  );
}

export default Pdfcomp;
