// BookList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReadBook from './ReadBook'; // Ensure this imports the component we will create next

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const res = await axios.get('http://localhost:4001/');
        setBooks(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchBooks();
  }, []);

  const handleReadClick = (bookId) => {
    setSelectedBook(bookId);
  };

  return (
    <div>
      <h1>Books List</h1>
      <ul>
        {books.map((book) => (
          <li key={book._id}>
            <span>{book.title}</span>
            <button onClick={() => handleReadClick(book._id)}>Read</button>
          </li>
        ))}
      </ul>
      {selectedBook && <ReadBook bookId={selectedBook} />}
    </div>
  );
};

export default BookList;
