import React, { createContext, useContext, useState, useEffect } from 'react';

export const AuthContext = createContext();

export default function AuthProvider({ children }) {
  const initialAuth = localStorage.getItem('Users');
  const [auth, setAuth] = useState(initialAuth ? JSON.parse(initialAuth) : undefined);

  // Sync auth state to localStorage whenever it changes
  useEffect(() => {
    if (auth) {
      localStorage.setItem('Users', JSON.stringify(auth));
    } else {
      localStorage.removeItem('Users');
    }
  }, [auth]);

  return (
    <AuthContext.Provider value={[auth, setAuth]}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
