import React from 'react'
import {useAuth} from "../Context/Auth"

const [auth,setAuth] = useAuth()

const Logoutkro =()=>{
   try{
      setAuth({
        ...auth,
        user:null

      })
      localStorage.removeItem("User")
      toast.sucess("Logout S")
   }catch{

   }
}
const Logout = () => {
  return (
    <div>
       <button className="py-2 px-4 bg-red-500 text-white rounded-lg cursor-pointer" onClick={Logoutkro}>Logout</button>

    </div>
  )
}

export default Logout
