// ReadBook.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ReadBook = ({ bookId }) => {
  const [pdfPath, setPdfPath] = useState('');

  useEffect(() => {
    const fetchBookPDF = async () => {
      try {
        const res = await axios.get(`http://localhost:4001/book/${bookId}/pdf`);
        setPdfPath(res.data.pdfPath); // Setting the PDF path
      } catch (err) {
        console.error(err);
      }
    };

    fetchBookPDF();
  }, [bookId]);

  return (
    <div>
      <h1>Read Book</h1>
      {pdfPath ? (
        <iframe 
          src={`http://localhost:4001${pdfPath}`} 
          width="600" 
          height="800" 
          title="PDF Viewer"
        />
      ) : (
        <p>Loading PDF...</p>
      )}
    </div>
  );
};

export default ReadBook;
