import toast from 'react-hot-toast';
import {useAuth} from "../Context/Auth"

function Logout(){
const [auth,setAuth] = useAuth()
const Logoutkro =()=>{
   try{
      setAuth({
        ...auth,
        user:null

      })
      localStorage.removeItem("User")
      toast.success("Logout Sucessfully")
   }catch{

   }
}
}

 


export default Logout
