import { useState } from 'react';
import { Document, Page } from 'react-pdf';
import { Link } from 'react-router-dom';

function Pdfcomp({ pdfFile }) {  // Destructure pdfFile from props
  const [numPages, setNumPages] = useState(null);  // Initialize with null
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <>
    

     <div className='m-5 p-5 mx-auto border border-rounded border-black max-w-screen-md dark:border-white overflow-hidden'>
    
        <p className='text-xl p-4 font-bold text-center'>
          Page {pageNumber} of {numPages || 'Loading...'} {/* Handle case when numPages is not yet loaded */}
        </p>

        {pdfFile ?  (  // Check if pdfFile is provided before rendering the document
          <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
            {numPages && Array.from({ length: numPages }, (x, i) => (
              <div key={i} className='mb-5 border border-gray-300 p-2 bg-blue-50 w-full overflow-hidden'>
                <Page
                  pageNumber={i + 1}
                  renderAnnotationLayer={false}
                  renderTextLayer={false}
                  width={600}
                  className='mx-auto w-full'
                />
              </div>
            ))}
          </Document>
        ) : (
          <p>No PDF file selected</p>  // Show a message if no pdfFile is provided
        )}
      </div>
      </>
   
  );
  
}

export default Pdfcomp;
