import React, { useEffect, useState } from 'react';
import axios from 'axios';
//import { pdfjs } from 'react-pdf';
//import Pdfcomp from "./Pdfcomp.js";



const Front = () => {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);  // Initialize with null for better checks
  const [allImage, setAllImage] = useState([]);
  const [pdfFile, setPDFFile] = useState(null); //

  useEffect(() => {
    getPdf();
  }, []);

  const getPdf = async () => {
    try {
      const result = await axios.get("http://localhost:4001/get-files");
      setAllImage(result.data.data);
    } catch (error) {
      console.error("Error fetching PDFs:", error.response ? error.response.data : error.message);
    }
  };

  const submitImage = async (e) => {
    e.preventDefault();

    if (!title.trim()) {
      alert('Title is required');
      return;
    }

    if (!file) {
      alert('Please upload a PDF file');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('file', file);

    try {
      const result = await axios.post("http://localhost:4001/upload-files", formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(result);
      getPdf();  // Refresh list after successful upload
    } catch (error) {
      console.error("Error uploading file:", error.response ? error.response.data : error.message);
    }
  };

  const showPdf = (pdf) => {
    window.open(`http://localhost:4001/files/${pdf}`, "_blank");
    //setPDFFile(`http://localhost:5000/files/${pdf}`);
  };

  return (
    <div>
      <div className='flex flex-col m-5 justify-center onsub'>
        <form className="flex flex-col items-center gap-3 border border-black p-5 rounded mx-auto" onSubmit={submitImage}>
          <h1 className="text-lg font-bold">Upload PDF</h1>
          
          <input
            type="text"
            className='border border-black p-2 rounded outline-none w-full'
            placeholder='Title'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          
          <input
            type="file"
            className='form-control w-full'
            accept='application/pdf'
            onChange={(e) => setFile(e.target.files[0])}
            required
          />
          
          <button type="submit" className='btn btn-primary'>
            Submit
          </button>
        </form>

        <div className='flex flex-col m-5 items-center upload'>
          <h1>Upload Here!</h1>
          <div className='flex flex-row gap-x-6 justify-center'>
            {allImage.length === 0 ? (
              <p>No PDFs available</p>
            ) : (
              allImage.map((data) => (
                <div className="inner" key={data.id}>
                  <h6>Title: {data.title}</h6>
                  <button type="button" className='btn btn-primary' onClick={() => showPdf(data.pdf)}>
                    View PDF
                  </button>
                </div>
              ))
            )}
          </div>
          
        </div>
      </div>
      
    </div>
  );
};

export default Front;
