import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { pdfjs } from 'react-pdf';

pdfjs.GlobalWorkerOptions.workerSrc = new URL(
    'pdfjs-dist/build/pdf.worker.min.mjs',
    import.meta.url,
).toString();

const PurchasedBooks = ({ item }) => {

    return (
        <div className='mt-4'>
            <div className="card bg-base-100 w-90 m-2 shadow-xl hover:scale-105 duration-200 bg-white text-black dark:bg-slate-900 dark:text-white dark:border">
                <figure>
                    <img src={item.image} alt="Book Cover" />
                </figure>
                <div className="card-body">
                    <h2 className="card-title">
                        {item.name}
                        <div className="badge badge-secondary">{item.category}</div>

                    </h2>
                    <p>Genre: {item.title}</p>
                    <p> Author: <span className="italic">{item.author}</span> </p>
                    <div className="card-actions justify-between">
                        <button className='rounded p-2 bg-slate-500 text-white'>Read Book</button>

                        {/* yaha pr hm ek showPDF() call krenge baad me */}

                        <div>                    

                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PurchasedBooks;
