import { useState } from 'react';
import { Document, Page } from 'react-pdf';
import { useLocation } from 'react-router-dom';

function Pdfcomp(props) {
  const [numPages, setNumPages] = useState();
  const [pageNumber, setPageNumber] = useState(1);

  // Get the passed state from the location object
  const location = useLocation();
  const { pdfFile } = location.state || {};  // Get the pdfFile from state or fallback to undefined

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div className='m-5 p-5 mx-auto border border-rounded border-black max-w-screen-md overflow-hidden'>
      <p className='text-xl font-bold text-center'>Page {pageNumber} of {numPages}</p>

      {pdfFile ? (
        <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess}>
          {Array.apply(null, Array(numPages)).map(function (x, i) {
            return (
              <div key={i} className='mb-5 border border-gray-300 p-2 bg-blue-50 w-full overflow-hidden'>
                <Page
                  pageNumber={i + 1}
                  renderAnnotationLayer={false}
                  renderTextLayer={false}
                  width={600} 
                  className='mx-auto w-full'
                />
              </div>
            );
          })}
        </Document>
      ) : (
        <p>No PDF file selected</p>
      )}
    </div>
  );
}

export default Pdfcomp;
