import axios from 'axios';
import React from 'react';
import { pdfjs } from 'react-pdf';
import { useNavigate } from 'react-router-dom';

pdfjs.GlobalWorkerOptions.workerSrc = new URL(
    'pdfjs-dist/build/pdf.worker.min.mjs',
    import.meta.url,
).toString();


const PurchasedBooks = ({ item }) => {

    const navigate = useNavigate();
    
    const showBook = (pdf) => {
        if (!pdf) {
            alert("No PDF file is available to Display")
            return
        }
        navigate(`/pdf/${encodeURIComponent(pdf)}`)
                
    }
    

    const book = item.bookId ;

    const deletePurchasedBook = async (bookId) => {
        try {
            const response = await axios.post("http://localhost:4001/purchase/delete-purchased-book", {
                bookId: bookId  
            });
            console.log(response.data); 
        } catch (error) {
            console.error('Delete failed:', error);
        }
    };
    

    
    console.log(item.id);
    

    return (
        <div className='mt-4'>
            <div className="card bg-base-100 w-90 m-2 shadow-xl hover:scale-105 duration-200 bg-white text-black dark:bg-slate-900 dark:text-white dark:border">
                <figure>
                    <img src={book.image} alt="Book Cover" />
                </figure>
                <div className="card-body">
                    <h2 className="card-title">
                        {book.name}
                        <div className="badge badge-secondary">{book.category}</div>

                    </h2>
                    <p>Genre: {book.title}</p>
                    <p> Author: <span className="italic">{book.author}</span> </p>
                    <div className="card-actions justify-between">
                        <button className='rounded p-2 bg-slate-500 text-white' onClick={ () => showBook(book.pdf) }>Read Book</button>
                        <div>                    
                        <button className='rounded p-2 bg-slate-500 text-white' onClick={ () => deletePurchasedBook(book.id) }>Delete</button>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};


export default PurchasedBooks;
