import React from 'react'

const Cards = ({item}) => {
  
  
  const showPdf = (pdf) => {
    //window.open(`http://localhost:4001/files/${pdf}`, "_blank");
    setPDFFile(`http://localhost:4001/files/${pdf}`);
  };
  return (
    <>
    <div className='mt-4'>
    <div className="  card bg-base-100 w-90 m-2 shadow-xl hover:scale-105 duration-200 bg-white text-black dark:bg-slate-900 dark:text-white dark:border ">
  <figure>
    <img
      src={item.image}
      alt="Shoes" />
  </figure>
  <div className="card-body">
    <h2 className="card-title">
      {item.name}
      <div className="badge badge-secondary">{item.category}</div>
    </h2>
    <p>{item.title}</p>
    <div className="card-actions justify-between">
      <div className="cursor-pointer px-7 py-1 rounded-full border-[2px] hover:bg-pink-500 hover:text-white duration-300 badge-outline" onClick={() => showPdf(data.pdf)}>${item.price}</div>
      <div className="cursor-pointer px-2 py-1 rounded-full border-[2px] hover:bg-pink-500 hover:text-white duration-300 badge-outline">Buy Now</div>
    </div>
  </div>
</div>
    </div>
    </>
   
  )
}

export default Cards
