import React, { useEffect, useState } from 'react';
import axios from 'axios';

export const Srijan = () => {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null); 
  const [allImage, setAllImage] = useState(null);

useEffect(() => {
  getPdf();
},[]);
 const getPdf = async()=>{
  const result = await axios.get("http://localhost:4001/get-file");
  console.log(result.data.data);
  setAllImage(result.data.data);
 };
  
  const showpdf=(pdf) => {
    window.open(`http://localhost:4001/files/${pdf}`, '_blank');
  }

  const submitImage = async (e) => {
    e.preventDefault();

 
    const formData = new FormData();
    formData.append('title', title); 
    formData.append('file', file);   
    
    try {
      const result = await axios.post("http://localhost:4001/upload-files", formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      console.log(JSON.stringify(result.data));
     
    } catch (error) {
      console.error('Error uploading file:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <>
    <div className='flex mt-5 justify-center'>
      <form className="formStyle space-y-3 border border-black p-5 rounded" onSubmit={submitImage}>
        <h1 className="text-lg">Upload PDF</h1>
        <input
          type="text"
          className='border border-black p-2 rounded outline-none'
          placeholder='Title'
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="file"
          className='form-control'
          accept='application/pdf'
          required
          onChange={(e) => setFile(e.target.files[0])} 
        />
        <button type="submit" className='btn btn-primary'>Submit</button>
      </form>

      
    </div>
    <form className='flex mt-5 justify-center' >
        <div className='uploaded flex space-x-4 justify-center '>

          {allImage==null?"":allImage.map((data) =>{
            return(
              
          <div className='inner-div  '>
             <h1>Uploaded PDF:</h1>
             <h6 className='justify-center'>Title :{data.title}</h6>
            <button className='btn primary-btn' onClick ={showpdf(data.pdf)}>View</button>
          </div>
            )
          })}

        </div>
      </form>
      </>
  );
}

export default Srijan;
