import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { pdfjs } from 'react-pdf';
import Pdfcomp from "./Pdfcomp";
import { Link } from 'react-router-dom';


pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();


const Srijan = () => {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);
  const [allImage, setAllImage] = useState([]);
  const navigate = useNavigate(); // Use the useNavigate hook

  useEffect(() => {
    getPdf();
  }, []);

  const showPdf = (pdfId) => {
    navigate(`/pdf/${pdfId}`); // Navigate to the PdfViewer route
  };

  return (
    <div>
      <div className='flex flex-col m-5 justify-center onsub'>
        <form className="flex flex-col items-center gap-3 border border-black p-5 rounded mx-auto" onSubmit={submitImage}>
          <h1 className="text-lg font-bold">Upload PDF</h1>

          <input
            type="text"
            className='border border-black p-2 rounded outline-none w-full'
            placeholder='Title'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <input
            type="file"
            className='form-control w-full'
            accept='application/pdf'
            onChange={(e) => setFile(e.target.files[0])}
            required
          />

          <button type="submit" className='btn btn-primary'>
            Submit
          </button>
        </form>

        <div className='flex flex-col m-5 mx-auto items-center upload'>
          <h1>Upload Here!</h1>
          <div className='flex flex-row gap-x-6 justify-center'>
            {allImage.length === 0 ? (
              <p>No PDFs available</p>
            ) : (
              allImage.map((data) => (
                <div className="inner" key={data.id}>
                  <h6>Title: {data.title}</h6>

                  <button type="button" className='btn btn-primary' onClick={() => showPdf(data.id)}>
                    View PDF
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};
