import React from 'react'
import { Link } from 'react-router-dom'

const About = () => {
  return (
    <div className="min-h-screen bg-white dark:bg-black text-gray-900 dark:text-white flex flex-col items-center justify-center px-4">
      
      {/* Heading Section */}
      <SectionHeader title="About BookStore" />
      
      {/* Description Section */}
      <SectionDescription 
        text="Welcome to BookStore! Our mission is to provide readers with access to a wide range of books, from timeless classics to the latest bestsellers. Whether you're an avid reader or just starting your literary journey, our platform is designed to make reading easy, affordable, and enjoyable." 
      />

      {/* Mission Section */}
      <SectionSubheading title="Our Mission" />
      <SectionDescription 
        text="At BookStore, we believe in the power of books to inspire, educate, and entertain. We aim to make books more accessible to people everywhere, offering a variety of free and paid books at your fingertips." 
      />

      {/* Features Section */}
      <SectionSubheading title="Features" />
      <ul className="list-disc pl-5 mb-6">
        <li>Read Free and Paid Books</li>
        <li>Simple and User-Friendly Interface</li>
        <li>Responsive Design for Any Device</li>
      </ul>

      {/* Back Button */}
      <Link to="/">
        <button className="bg-pink-500 mt-4 px-4 py-2 rounded-md text-white hover:bg-pink-700 duration-300">
          Back to Home
        </button>
      </Link>
    </div>
  )
}

// Component for Section Heading
const SectionHeader = ({ title }) => {
  return <h1 className="text-3xl font-bold mb-4">{title}</h1>
}

// Component for Section Subheading
const SectionSubheading = ({ title }) => {
  return <h2 className="text-2xl font-semibold mb-2">{title}</h2>
}

// Component for Section Description
const SectionDescription = ({ text }) => {
  return <p className="mb-4 text-center max-w-2xl">{text}</p>
}

export default About
